package Loops;

public class StarLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            char c= '*';
		//int star=1;
             int i;int j;
		for (i=0;i<=6;i++)
		{
			for(j=0;j<i;j++)
			{
			System.out.print(c );
			}
			
			System.out.println(" ");
		}
	}

}
